package x509

import (
	"encoding/hex"
	"testing"
)

var pkcs8PrivateKeyHex = ``

func TestPKCS8(t *testing.T) {
	derBytes, _ := hex.DecodeString(pkcs8PrivateKeyHex)
	_, err := ParsePKCS8PrivateKey(derBytes)
	if err != nil {
		t.Errorf("failed to decode PKCS8 key: %s", err)
	}
}
